import React from 'react';
import ReactDOM from 'react-dom';

// Functional component:
// const App = () => {
//     window.navigator.geolocation.getCurrentPosition(
//         (position) => console.log(position),
//         (err) => console.log(err)
//     )
//     return (
//     <div>Latitude: </div>
//     )
// }

// Class based component
// We are subclassing React.Component, basically borrowing functionality into our App class
class App extends React.Component {
    // define constructor method
    constructor(props) {
        super(props);
        // this is the ONLY time we do direct assignment to state
        this.state = {lat: null };

        window.navigator.geolocation.getCurrentPosition(
            (position) => {
                // we called setState which is a react component!
                this.setState( { lat: position.coords.latitude })
            },
            (err) => console.log(err)
        )
    }
    // We have to define render when using React
    render() {

        return <div>Latitude: {this.state.lat}</div>
    }
}


ReactDOM.render(<App />, document.getElementById('root'));
